package com.corejava_program;

public class Nodecreasing_reversepyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int t=6;
      for(int i=t;i>0;i--) {
    	  for(int k=0;k<i;k++) {
    		  System.out.print("*");
    	  }
    	  System.out.println();
      }
	}

}
