package com.corejava_program;

public class pattern5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int rows=5;
       for(int i=0;i<=rows;i++) {
    	   for(int j=0;j<i;j++) {
    		   System.out.print("");
    	   }
    	   for(int k=i;k<rows;k++) {
    	   System.out.println("");
       }
	
	}
	}
}
